package com.yn.module.service;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.AboutEvent;import com.yn.module.mapper.AboutEventMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * @Author: xw * @Description: * @Date: 13:01 2018/9/21 */@Servicepublic class AboutEventService {    @Autowired    private AboutEventMapper aboutEventMapper;    @Transactional(readOnly = true)    public AboutEvent getAboutEvent(AboutEvent aboutEvent) throws Exception{        return aboutEventMapper.selectAboutEvent(aboutEvent);    }}