package com.yn.module.service;import com.github.pagehelper.PageInfo;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.AboutUs;import com.yn.module.mapper.AboutUsMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * @Author: xw * @Description: * @Date: 17:55 2018/9/26 */@Servicepublic class AboutUsService {    @Autowired    private AboutUsMapper aboutUsMapper;    @Transactional(readOnly = true)    public ResultResponse getAboutUsList(AboutUs aboutUs){        return ResultResponse.successList(new PageInfo<AboutUs>(aboutUsMapper.selectAboutUsList(aboutUs)), aboutUsMapper.selectAboutUsCount(aboutUs));    }}