package com.yn.module.service;import com.github.pagehelper.PageInfo;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.CmsArticle;import com.yn.module.mapper.CmsArticleMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.ArrayList;import java.util.List;/** * @Author: xw * @Description: * @Date: 11:35 2018/9/30 */@Servicepublic class CmsArticleService {    @Autowired    private CmsArticleMapper cmsArticleMapper;    @Transactional(readOnly = true)    public ResultResponse getCmsArticleList(CmsArticle cmsArticle){        ResultResponse result = ResultResponse.success(new ArrayList<>());        List<CmsArticle> list = cmsArticleMapper.selectCmsArticleList(cmsArticle);        Integer count = cmsArticleMapper.selectCmsArticleCount(cmsArticle);        return ResultResponse.successList(new PageInfo<>(list), count);    }}