package com.yn.module.service;import com.github.pagehelper.PageInfo;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.ExhibitTeam;import com.yn.module.mapper.ExhibitTeamMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * @Author: xw * @Description: * @Date: 17:44 2018/10/5 */@Service@Transactional(readOnly = true)public class ExhibitTeamService {    @Autowired    private ExhibitTeamMapper exhibitTeamMapper;    public ResultResponse getExhibitTeamList(ExhibitTeam exhibitTeam) throws Exception{        return ResultResponse.successList(new PageInfo<ExhibitTeam>(exhibitTeamMapper.selectExhibitTeamList(exhibitTeam)), exhibitTeamMapper.selectExhibitTeamCount(exhibitTeam));    }}