package com.yn.module.service;import com.github.pagehelper.PageInfo;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.*;import com.yn.module.mapper.FocusImageMapper;import com.yn.module.mapper.HomeLogoMapper;import com.yn.module.mapper.HomeShowMapper;import org.apache.commons.beanutils.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;/** * @Author: xw * @Description: * @Date: 1:11 2018/10/3 */@Service@Transactional(readOnly = true)public class HomeService {    @Autowired    private HomeLogoMapper homeLogoMapper;    @Autowired    private HomeShowMapper homeShowMapper;    @Autowired    private FocusImageMapper focusImageMapper;    public ResultResponse getHomeLog(HomeLogo homeLogo) throws Exception{        return ResultResponse.success(homeLogoMapper.selectHomeLogo(homeLogo));    }    public ResultResponse getHomeShowList(HomeShow homeShow){        return ResultResponse.successList(new PageInfo<HomeShow>(homeShowMapper.selectHomeShowList(homeShow)), homeShowMapper.selectHomeShowCount(homeShow));    }    public ResultResponse getHomeLogEWM(HomeLogo homeLogo) throws Exception{        return ResultResponse.success(homeLogoMapper.selectHomeLogo(homeLogo));    }    public ResultResponse getFocusImage(FocusImage focusImage) throws Exception{        return ResultResponse.success(focusImageMapper.selectFocusImage(focusImage));    }}