package com.yn.module.service;import com.github.pagehelper.PageInfo;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.VisiterOption;import com.yn.module.mapper.ComboMapper;import com.yn.module.mapper.VisiterOptionMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @Author: xw * @Description: * @Date: 12:34 2018/10/5 */@Service@Transactional(readOnly = true)public class VisiterOptionService {    @Autowired    private VisiterOptionMapper visiterOptionMapper;    @Autowired    private ComboMapper comboMapper;    public ResultResponse getVisiterOptionList(String language) throws Exception{        VisiterOption visiterOption = new VisiterOption();        Map<String,Object> result  = new HashMap<>();        Map<String,Object> comboParam  = new HashMap<>();        visiterOption.setLan(language);        comboParam.put("lan", language);        visiterOption.setCategory("0");        List<VisiterOption> areaList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        visiterOption.setCategory("1");        List<VisiterOption> conferenceList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        visiterOption.setCategory("2");        List<VisiterOption> whereList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        comboParam.put("type", "country");        List<Map<String, Object>> countryList = comboMapper.selectComboList(comboParam);        comboParam.put("type", "businessType");        List<Map<String, Object>> businessTypeList = comboMapper.selectComboList(comboParam);        result.put("areaList", areaList);        result.put("conferenceList", conferenceList);        result.put("whereList", whereList);        result.put("countryList", countryList);        result.put("businessTypeList", businessTypeList);        return ResultResponse.success(result);    }}