package com.yn.module.service;import com.yn.common.msg.ResultResponse;import com.yn.module.entity.VisiterOption;import com.yn.module.mapper.VisiterOptionMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @Author: xw * @Description: * @Date: 12:34 2018/10/5 */@Service@Transactional(readOnly = true)public class VisiterOptionService {    @Autowired    private VisiterOptionMapper visiterOptionMapper;    public ResultResponse getVisiterOptionList(/*VisiterOption visiterOption*/) throws Exception{        VisiterOption visiterOption = new VisiterOption();        Map<String,Object> result  = new HashMap<>();        visiterOption.setCategory("0");        List<VisiterOption> areaList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        visiterOption.setCategory("1");        List<VisiterOption> conferenceList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        visiterOption.setCategory("2");        List<VisiterOption> whereList = visiterOptionMapper.selectVisiterOptionList(visiterOption);        result.put("areaList", areaList);        result.put("conferenceList", conferenceList);        result.put("whereList", whereList);        return ResultResponse.success(result);    }}